mapper.select:
SELECT t.ID, t.NAME, tr.TITLE, t.MOBILE, t.EMAIL, t.AVATAR FROM TBL_USER t, TBL_USER_ROLE tr WHERE t.IS_DELETED = 0 AND tr.IS_DELETED = 0 AND t.ID = tr.USER_ID AND tr.ROLE = 'TEACHER'
AND t.NAME LIKE concat("%",#{name},"%")
LIMIT #{page.start}, #{page.limit}

SELECT t.ID, t.NAME, tr.TITLE, t.MOBILE, t.EMAIL, t.AVATAR FROM TBL_USER t, TBL_USER_ROLE tr WHERE t.IS_DELETED = 0 AND tr.IS_DELETED = 0 AND t.ID = tr.USER_ID AND tr.ROLE = 'TEACHER' AND t.ID IN
#{userId}
LIMIT #{page.start}, #{page.limit}

